table_dat |>
  mutate(variable = gsub('initial_weight_g','initial weight (g)',variable),
    across(where(is.numeric),~round(.,3))) |>
  pivot_wider(names_from = model,values_from = c(estimate,SE,`p-value`)) |>
  gt() |>
  tab_spanner(
    label = "Development Time",
    columns = ends_with('development time'))  |>
  tab_spanner(
    label = "Specific Growth Rate",
    columns = ends_with('specific growth rate')) |>
  cols_label(
    starts_with("est") ~ "estimate",
    starts_with("SE") ~ "SE",
    starts_with("p-value") ~ "p-value"
  ) |>
  sub_missing(
    columns = everything(),
    missing_text = ""
  )
